import React from "react";
import { Formik } from "formik";

import { Modal, Button } from "react-bootstrap";
import { TextField, SubmitBtn } from "../../components/Form";

const NewItemCategory = ({ show, setShow, category, schema, handleSubmit }) => {
  return (
    <Formik
      enableReinitialize={true}
      initialValues={category}
      validationSchema={schema}
      onSubmit={(category, { resetForm }) => {
        resetForm();
        handleSubmit(category);
      }}
    >
      {() => (
        <>
          <Modal show={show}>
            <Modal.Header>
              <Modal.Title>
                {category.id === 0 ? "New Category" : "Update Category"}
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <TextField name="name" label="Name" required />
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={() => setShow(false)} className="btn-secondary">
                Close
              </Button>
              <SubmitBtn />
            </Modal.Footer>
          </Modal>
        </>
      )}
    </Formik>
  );
};

export default NewItemCategory;
