import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { Card, Row, Col } from "react-bootstrap";
import { MDBDataTableV5 } from "mdbreact";
import {
    SelectField,
    SubmitBtn,
} from "../../components/Form";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import { toast } from "react-toastify";
import Fontawesome from "react-fontawesome";

class ChecklistForm extends Component {
    state = {
        chechlist: {
            id: 0,
            equibmentId: "",
            equibmenTypeId: "",
            shift: "",
        },
        equibments: [
            { value: 1, label: "FORKLIFT" },
            { value: 2, label: "ITV" },
            { value: 3, label: "MHC" },
            { value: 4, label: "MC" },
            { value: 5, label: "RTG" },
            { value: 6, label: "ECH" },
            { value: 7, label: "RS" }
        ],
        shifts: [
            { value: 1, label: "7:00 - 11:00 am" },
            { value: 2, label: "12:00 - 4:00 pm" },
            { value: 3, label: "5:00 - 7:00 pm" },
            { value: 4, label: "7:00 - 11:00 pm" },
            { value: 5, label: "12:00 - 4:00 am" },
            { value: 6, label: "5:00 - 7:00 am" }
        ],
        forklifts: [],
        ITVes: [],
        MHCes: [],
        MCes: [],
        RTGes: [],
        ECHes: [],
        RSes: [],
        equibmentTypes: [],
        forkliftCheck: [
            { value: "hydraulicControls", label: "Hydraulic controls", },
            { value: "steering ", label: "Steering", },
            { value: "handBreak ", label: "Hand Break", },
            { value: "lights ", label: "Lights", },
            { value: "hornsAndAlarms", label: "Horns/ Alarms ", },
            { value: "seatBelts", label: "Seat belts ", },
            { value: "noTyreDamageOrInsuficientInflation", label: "No tyre damage or insuficient inflation", },
            { value: "noWheelsLooseNuts", label: "No Wheels- loose nuts", },
            { value: "noAccidentDamageOrFrameIncludingAttachements ", label: "No accident damage or frame including attachements", },
            { value: "noExcessiveFluidLeaksEspeciallyUnderMachine", label: "No excessive fluid leaks especially under machine ", },
            { value: "floodLightsAreWorking ", label: "Flood lights are working ", },
            { value: "tailLightsAreWorking ", label: "Tail lights are working", },
            { value: "mirrors ", label: "Mirrors", },
            { value: "rotatingAmbersLightsAreWorking ", label: "Rotating ambers lights are working ", },
            { value: "fireExtingisher", label: "Fire extingisher", },
        ],
        tableHeaders: [
            { label: "Name", field: "label" },
            { label: "", field: "edit" },
        ],
    };

    schema = Yup.object({
        id: Yup.number().label("Id"),
        customerId: Yup.number().required().label("Customer"),
        roomId: Yup.number().required().label("Room"),
        amount: Yup.number().required().min(0).max(10000).label("Amount"),
        startDate: Yup.date().required().label("Start Date"),
    });

    render() {
        const { chechlist, equibments, shifts, tableHeaders, forkliftCheck } = this.state;


        return (
            <>
                <Card>
                    <Card.Header>
                        <Card.Title>
                            {chechlist.id === 0 ? "New checklist" : "Update checklist"}
                        </Card.Title>
                    </Card.Header>
                    <Formik
                        initialValues={chechlist}
                        enableReinitialize={true}
                        onSubmit={(chechlist, { resetForm }) => {
                            resetForm();
                            this.setState({ chechlist });
                            this.handleSubmit(chechlist);
                        }}
                    >
                        {({ values }) => (
                            <>
                                <Card.Body>
                                    <Row>
                                        <Col>
                                            <SelectField
                                                name="equibmentId"
                                                label="Equibment"
                                                options={equibments}
                                                required
                                            />
                                        </Col>
                                        <Col>
                                            <SelectField
                                                name="shiftId"
                                                label="Shift"
                                                options={shifts}
                                                required
                                            />
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col>
                                            <SelectField
                                                name="equibmentTypeId"
                                                label="Equibment Type"
                                                options={this.handleEquibmentType(values['equibmentId']).map((fork) => ({ label: fork.name, value: fork.id }))}
                                                required
                                            />
                                        </Col>
                                    </Row>
                                    <MDBDataTableV5
                                        hover={false}
                                        entries={15}
                                        pagesAmount={15}
                                        data={{
                                            columns: tableHeaders,
                                            rows: forkliftCheck.map((obj) => {
                                                obj.edit = <input type="checkbox" className="form-control mr-5" onChange={() => this.handleChecklist(obj.value)} />


                                                return obj;
                                            }),
                                        }}
                                        searchTop={false}
                                        searchBottom={false}
                                    />
                                    <Row>

                                    </Row>
                                </Card.Body>
                                <Card.Footer className="text-center">
                                    <SubmitBtn />
                                </Card.Footer>
                            </>
                        )}
                    </Formik>
                </Card>
            </>
        );
    }

    handleChecklist = (value) => {
        const { forkliftCheck } = this.state;
        const data = forkliftCheck.map(f => {
            if (f.value === value) {
                f.isTrue = f.isTrue ? false : true;

                return f;
            }

            return f;
        })

        this.setState({ forkliftCheck: data });

    }

    handleEquibmentType = (id) => {
        const { forklifts, ITVes, MHCes, MCes, RTGes, ECHes, RSes } = this.state;


        if (id === 1) {

            return forklifts;
        }
        else if (id === 2) {

            return ITVes;
        }
        else if (id === 3) {

            return MHCes;
        }
        else if (id === 4) {

            return MCes;
        }
        else if (id === 5) {

            return RTGes;
        }
        else if (id === 6) {

            return ECHes;
        }
        else if (id === 7) {

            return RSes;
        }
        else {
            return []
        }
    }

    componentDidMount() {
        const auth = { Authorization: `bearer ${localStorage["token"]}` };


        Services.get(Config.apiUrl + "/forklift", { headers: auth })
            .then(({ data }) => {
                this.setState({ forklifts: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/itv", { headers: auth })
            .then(({ data }) => {
                this.setState({ ITVes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/mhc", { headers: auth })
            .then(({ data }) => {
                this.setState({ MHCes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/mc", { headers: auth })
            .then(({ data }) => {
                this.setState({ MCes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/rtg", { headers: auth })
            .then(({ data }) => {
                this.setState({ RTGes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/ech", { headers: auth })
            .then(({ data }) => {
                this.setState({ ECHes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/rs", { headers: auth })
            .then(({ data }) => {
                this.setState({ RSes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });
    }

    handleSubmit = () => {
        const { chechlist, forkliftCheck } = this.state;
        const auth = { Authorization: `bearer ${localStorage["token"]}` };

        console.log(forkliftCheck);

        // Services.post(Config.apiUrl + "/rentals", data, { headers: auth })
        //     .then(() => {
        //         toast.success("Successful Registred.");
        //     })
        //     .catch((error) => {
        //         console.log(error);

        //         if (error.response && error.response.data) {
        //             toast.error(error.response.data);
        //         } else {
        //             toast.error("Something went wrong");
        //         }
        //     });
    };
}

export default ChecklistForm;
