import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { Card, Row, Col } from "react-bootstrap";
import { MDBDataTableV5 } from "mdbreact";
import {
    SelectField,
    SubmitBtn,
} from "../../components/Form";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import { toast } from "react-toastify";
import Fontawesome from "react-fontawesome";
import Select from "react-select";
import { FormGroup, FormLabel } from "react-bootstrap";

class ChecklistForm extends Component {
    state = {
        chechlist: {
            id: 0,
            equibmentId: "",
            equibmenTypeId: "",
            shift: "",
        },
        tableHeaders: [
            { label: "Name", field: "label" },
            { label: "", field: "edit" },
        ],
        equibments: [
            { value: 1, label: "FORKLIFT" },
            { value: 2, label: "ITV" },
            { value: 3, label: "MHC" },
            { value: 4, label: "MC" },
            { value: 5, label: "RTG" },
            { value: 6, label: "ECH" },
            { value: 7, label: "RS" }
        ],
        shifts: [
            { value: 1, label: "7:00 - 11:00 am" },
            { value: 2, label: "12:00 - 4:00 pm" },
            { value: 3, label: "5:00 - 7:00 pm" },
            { value: 4, label: "7:00 - 11:00 pm" },
            { value: 5, label: "12:00 - 4:00 am" },
            { value: 6, label: "5:00 - 7:00 am" }
        ],
        forklifts: [],
        ITVes: [],
        MHCes: [],
        MCes: [],
        RTGes: [],
        ECHes: [],
        RSes: [],
        equibmentTypes: [],
