        const { forklifts, forkliftCheck, ITVes, ITVCheck, MHCes, MCes, RTGes, ECHes, RSes } = this.state;

        if (id === 1) {

            this.setState({ equibmentTypes: forklifts, CheckLists: forkliftCheck })
        }
        else if (id === 2) {

            this.setState({ equibmentTypes: ITVes, CheckLists: ITVCheck })
        }
        else if (id === 3) {

            this.setState({ equibmentTypes: MHCes })
        }
        else if (id === 4) {

            this.setState({ equibmentTypes: MCes })
        }
        else if (id === 5) {

            this.setState({ equibmentTypes: RTGes })
        }
        else if (id === 6) {

            this.setState({ equibmentTypes: ECHes })
        }
        else if (id === 7) {

            this.setState({ equibmentTypes: RSes })
        }
        else {
            return []
        }
    }

    handleChecklist = (value) => {
        const { forkliftCheck } = this.state;
        const data = forkliftCheck.map(f => {
