import React, { Component } from "react";
import { Card, Row, Col, FormLabel } from "react-bootstrap";
import Fontawesome from "react-fontawesome";
import { MDBDataTableV5 } from "mdbreact";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import PrinTible from "../../Utility/printible";
import ReactToPrint from "react-to-print";
import Select from "react-select";
import DateTime from "react-datetime";
import Filter from "../../Utility/servicesFilter";

class Forklifts extends Component {
  state = {
    tableHeaders: [
      { label: "Name", field: "name" },
      { label: "Date", field: "date" },
      { label: "", field: "edit" },
    ],
    services: [],
    categories: [],
    rooms: [],
    filter: {
      name: "All",
      date: "",
    },
    show: false,
    items: [],
    allItems: [],
  };

  render() {
    const {
      tableHeaders,
      services: allservices,
      categories,
      filter,
      show,
      items,
      allItems,
    } = this.state;

    const services = Filter(allservices, filter);

    return (
      <>
        <Card>
          <Card.Header>
            <Card.Title>
              Forklift Report
              <ReactToPrint
                trigger={() => (
                  <button
                    type="button"
                    className="btn d-print-none float-right"
                  >
                    <Fontawesome
                      className="fas fa-print"
                      name="print"
                      style={{ fontSize: 25 }}
                    />
                  </button>
                )}
                content={() => {
                  return this.componentRef;
                }}
              />
              <div className="d-none">
                <PrinTible
                  data={services.map((r) => ({
                    id: r.id,
                    category: r.category,
                    date: new Date(r.Date).toDateString(),
                  }))}
                  theaders={tableHeaders
                    .filter((t) => t.label !== "")
                    .map((h) => h.label)}
                  title="Rental Report"
                  ref={(el) => (this.componentRef = el)}
                />
              </div>
            </Card.Title>
          </Card.Header>
          <Card.Body>
            <MDBDataTableV5
              hover
              entriesOptions={[10, 25, 50, 100]}
              entries={10}
              pagesAmount={10}
              data={{
                columns: tableHeaders,
                rows: services.map((ren) => {
                  ren.edit = (
                    <>
                      <a
                        className="ml-4"
                        onClick={() => this.handleShowModal(ren)}
                      >
                        <Fontawesome
                          className="fas fa-eye"
                          style={{ fontSize: 17 }}
                          name="edit"
                        />
                      </a>
                    </>
                  );
                  ren.total =
                    "$" +
                    ren.items.map((r) => r.amount).reduce((a, b) => a + b, 0);
                  ren.date = new Date(ren.date).toDateString();

                  return ren;
                }),
              }}
              pagingTop
              searchTop={false}
              searchBottom={false}
            />
          </Card.Body>
        </Card>
      </>
    );
  }

  componentDidMount() {
    this.setState({ loading: true });
    const auth = { Authorization: `bearer ${localStorage["token"]}` };

    Services.get(Config.apiUrl + "/forkliftcheck", { headers: auth }).then(
      ({ data }) => {
        this.setState({
          services: data.map((s) => {
            s.category = s.category.name;

            return s;
          }),
        });
      }
    );
  }
}

export default Forklifts;
