import React, { useEffect, useState } from "react";
import { Row, Col, Card, Table } from "react-bootstrap";

import Config from "../../config/config.json";
import Services from "../../services/HttpServices";

const Dashboard = () => {
  const [receipts, setReceipts] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [rentals, setRentals] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [budgetBalance, setBudgetBalance] = useState(0);
  const [services, setServices] = useState([]);

  const [snapshots, setSnapshots] = useState([]);
  const [users, setUsers] = useState([]);
  const [forklifts, setForklifts] = useState([]);
  const [itves, setItves] = useState([]);

  useEffect(() => {
    const auth = { Authorization: `bearer ${localStorage["token"]}` };

    Services.get(Config.apiUrl + "/SnapShot", { headers: auth }).then(
      ({ data }) => {
        setSnapshots([...data]);
      }
    );

    Services.get(Config.apiUrl + "/users", { headers: auth }).then(
      ({ data }) => {
        setUsers([...data]);
      }
    );

    Services.get(Config.apiUrl + "/forklift", { headers: auth }).then(
      ({ data }) => {
        setForklifts([...data]);
      }
    );

    Services.get(Config.apiUrl + "/itv", { headers: auth }).then(({ data }) => {
      setItves([...data]);
    });

    Services.get(Config.apiUrl + "/services", { headers: auth }).then(
      ({ data }) => {
        setServices([
          ...data
            .reverse((a, b) => new Date(a.date) + new Date(b.date))
            .slice(0, 7),
        ]);
      }
    );

    Services.get(Config.apiUrl + "/budget/balance", { headers: auth }).then(
      ({ data }) => {
        setBudgetBalance(data);
      }
    );
  }, []);

  return (
    <React.Fragment>
      <Row>
        <Col md={4} sm={6}>
          <Card className="user-card">
            <Card.Body>
              <h5 className="m-b-15">Forklifts</h5>
              <h4 className="f-w-300">{forklifts.length}</h4>
              <span className="text-muted">Monthly Invoice</span>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4} sm={6}>
          <Card className="user-card">
            <Card.Body>
              <h5 className="f-w-400 m-b-15">Total Receipts</h5>
              <h4 className="f-w-300">{itves.length}</h4>
              <span className="text-muted">
                <label className="label theme-bg text-white f-12 f-w-400">
                  {receipts.length > 0 && invoices.length > 0
                    ? Math.floor(
                        (receipts.map((r) => r.amount).reduce((a, b) => a + b) /
                          invoices
                            .map((i) => i.amount)
                            .reduce((a, b) => a + b)) *
                          100
                      )
                    : 0}
                  %
                </label>{" "}
                Monthly Receipt
              </span>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="user-card">
            <Card.Body>
              <h5 className="f-w-400 m-b-15">Rentals</h5>
              <h4 className="f-w-300">
                {rentals.filter((r) => r.isCurrent).length}
              </h4>
              <span className="text-muted">
                <label className="label theme-bg text-white f-12 f-w-400">
                  {(rentals.filter((r) => r.isCurrent).length / rooms.length) *
                    100}
                  %
                </label>
                Current Rent
              </span>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col sm={8}>
          <Card className="user-list">
            <Card.Header>
              <Card.Title as="h5">Recent Snapshots</Card.Title>
            </Card.Header>
            <Card.Body className="pb-0">
              <Table responsive hover>
                <thead>
                  <tr>
                    <th>Category</th>
                    <th>Location</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {snapshots.map((s) => (
                    <tr>
                      <td>
                        <h6 className="mb-1">{s.category}</h6>
                      </td>
                      <td>
                        <span className="pie_1">{s.location}</span>
                      </td>
                      <td>
                        <h6 className="m-0">
                          {new Date(s.date).toDateString()}
                        </h6>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={4}>
          <Card>
            <Card.Header>
              <Card.Title className="m-0">Users</Card.Title>
            </Card.Header>
            <Card.Body className="border-bottom">
              <div className="row align-items-center">
                <div className="col-8">
                  <h2 className="f-w-300 m-0">{users.length}</h2>
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </React.Fragment>
  );
};

export default Dashboard;
