import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { Card, Row, Col } from "react-bootstrap";
import {
  SelectField,
  SubmitBtn,
  TextField,
  DateField,
} from "../../components/Form";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import { toast } from "react-toastify";

class ChecklistForm extends Component {
  state = {
    chechlist: {
      id: 0,
      customerId: "",
      roomId: "",
      amount: "",
      startDate: "",
    },
    customers: [],
    rooms: [],
    file: "",
    show: false,
  };

  schema = Yup.object({
    id: Yup.number().label("Id"),
    customerId: Yup.number().required().label("Customer"),
    roomId: Yup.number().required().label("Room"),
    amount: Yup.number().required().min(0).max(10000).label("Amount"),
    startDate: Yup.date().required().label("Start Date"),
  });

  render() {
    const { chechlist, customers, rooms, show } = this.state;


    return (
      <>
        <Card>
          <Card.Header>
            <Card.Title>
              {chechlist.id === 0 ? "New checklist" : "Update checklist"}
            </Card.Title>
          </Card.Header>
          <Formik
            initialValues={chechlist}
            enableReinitialize={true}
            validationSchema={this.schema}
            onSubmit={(chechlist, { resetForm }) => {
              resetForm();
              this.setState({ chechlist });
              this.handleSubmit(chechlist);
            }}
          >
            {() => (
              <>
                <Card.Body>
                  <Row>
                    <Col>
                      <SelectField
                        name="customerId"
                        label="Customer"
                        options={customers.map((c) => ({
                          label: c.name,
                          value: c.id,
                        }))}
                        required
                        show={true}
                        onShow={this.handleShow}
                      />
                    </Col>
                    <Col>
                      <SelectField
                        name="roomId"
                        label="Room"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <TextField
                        name="amount"
                        label="Amount"
                        required
                        type="number"
                      />
                    </Col>
                    <Col>
                      <DateField name="startDate" label="start Date" required />
                    </Col>
                  </Row>
                </Card.Body>
                <Card.Footer className="text-center">
                  <SubmitBtn />
                </Card.Footer>
              </>
            )}
          </Formik>
        </Card>
      </>
    );
  }

  componentDidMount() {
    const auth = { Authorization: `bearer ${localStorage["token"]}` };
    const { id } = this.props.match.params;

    if (id) {
      Services.get(Config.apiUrl + "/rentals/" + id, { headers: auth })
        .then(({ data }) => {
          delete data.endDate;
          delete data.isCurrent;
          delete data.customer;
          delete data.room;

          this.setState({ file: data.document });
          delete data.document;
          this.setState({ chechlist: data });
        })
        .catch((error) => {
          console.log(error);

          toast.error("Something went wrong");
        });
    }

    Services.get(Config.apiUrl + "/customers", { headers: auth })
      .then(({ data }) => {
        this.setState({ customers: data });
      })
      .catch((error) => {
        console.log(error);

        toast.error("Something went wrong");
      });

    Services.get(Config.apiUrl + "/rooms", { headers: auth })
      .then(({ data }) => {
        this.setState({ rooms: data });
      })
      .catch((error) => {
        console.log(error);

        toast.error("Something went wrong");
      });
  }
  handleSubmit = () => {
    const { chechlist, file } = this.state;
    const auth = { Authorization: `bearer ${localStorage["token"]}` };
    const data = { ...chechlist, document: file };

    this.setState({
      chechlist: {
        id: 0,
        customerId: "",
        roomId: "",
        amount: "",
        startDate: chechlist.startDate,
      },
      file: "",
    });

    if (chechlist.id === 0) {
      Services.post(Config.apiUrl + "/rentals", data, { headers: auth })
        .then(() => {
          toast.success("Successful Registred.");
        })
        .catch((error) => {
          console.log(error);

          if (error.response && error.response.data) {
            toast.error(error.response.data);
          } else {
            toast.error("Something went wrong");
          }
        });
    } else {
      Services.put(Config.apiUrl + "/rentals/" + data.id, data, {
        headers: auth,
      })
        .then(() => {
          toast.info("Successful Updates.");
        })
        .catch((error) => {
          console.log(error);

          if (error.response && error.response.data) {
            toast.error(error.response.data);
          } else {
            toast.error("Something went wrong");
          }
        });
    }
  };
}

export default ChecklistForm;
