import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { Card, Row, Col } from "react-bootstrap";
import {
  SelectField,
  SubmitBtn,
  TextField,
  UploadFileField,
  DateField,
  TextAreaField,
} from "../../components/Form";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import { toast } from "react-toastify";

class Snapshot extends Component {
  state = {
    snapshot: {
      id: 0,
      customerId: "",
      roomId: "",
      amount: "",
      startDate: "",
    },
    customers: [],
    rooms: [],
    file: "",
  };

  schema = Yup.object({
    id: Yup.number().label("Id"),
    customerId: Yup.number().required().label("Customer"),
    roomId: Yup.number().required().label("Room"),
    amount: Yup.number().required().min(0).max(10000).label("Amount"),
    startDate: Yup.date().required().label("Start Date"),
  });

  render() {
    const { snapshot, customers, rooms, file } = this.state;
    const fileImage = URL.createObjectURL(
      new Blob([this.handleStringToArray(file)], { type: "image/png" })
    );

    return (
      <>
        <Card>
          <Card.Header>
            <Card.Title>
              {snapshot.id === 0 ? "New Snapshot" : "Update Snapshot"}
            </Card.Title>
          </Card.Header>
          <Formik
            initialValues={snapshot}
            enableReinitialize={true}
            validationSchema={this.schema}
            onSubmit={(snapshot, { resetForm }) => {
              resetForm();
              this.setState({ snapshot });
              this.handleSubmit(snapshot);
            }}
          >
            {() => (
              <>
                <Card.Body>
                  <Row>
                    <Col>
                      <SelectField
                        name="category"
                        label="Category"
                        options={customers.map((c) => ({
                          label: c.name,
                          value: c.id,
                        }))}
                        required
                        show={true}
                        onShow={this.handleShow}
                      />
                    </Col>
                    <Col>
                      <SelectField
                        name="incidentTyp"
                        label="Incident Type"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <SelectField
                        name="location"
                        label="Location"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                    <Col>
                      <SelectField
                        name="shift"
                        label="shift"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <SelectField
                        name="peopleInvoved"
                        label="People Involved"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                    <Col>
                      <SelectField
                        name="equibment"
                        label="shift"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <SelectField
                        name="responsible"
                        label="Responsible"
                        options={rooms.map((c) => ({
                          label: c.roomNumber,
                          value: c.id,
                        }))}
                        required
                      />
                    </Col>
                    <Col>
                      <TextField
                        name="action"
                        label="Action on site"
                        required
                        type="number"
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <TextField
                        name="action"
                        label="Action on site"
                        required
                        type="number"
                      />
                    </Col>
                    <Col>
                      <UploadFileField
                        name="document"
                        file="file"
                        label="Document"
                        type="file"
                        setFile={this.handleFile}
                      />
                      {file && (
                        <a href={fileImage} download>
                          <img
                            width="400"
                            height="400"
                            src={fileImage}
                            alt=""
                          />
                        </a>
                      )}
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <TextAreaField
                        name="action"
                        label="Action on site"
                        required
                        type="number"
                      />
                    </Col>
                  </Row>
                </Card.Body>
                <Card.Footer className="text-center">
                  <SubmitBtn />
                </Card.Footer>
              </>
            )}
          </Formik>
        </Card>
      </>
    );
  }

  handlePapulate = (customer) => {
    const { customers, snapshot } = this.state;
    snapshot.customerId = customer.id;

    this.setState({ customers: [customer, ...customers], snapshot });
  };
  handleClose = () => {
    this.setState({ show: false });
  };
  handleShow = () => {
    this.setState({ show: true });
  };
  handleFile = (file) => {
    this.setState({ file });
  };
  componentDidMount() {
    const auth = { Authorization: `bearer ${localStorage["token"]}` };
    const { id } = this.props.match.params;

    if (id) {
      Services.get(Config.apiUrl + "/snapshots/" + id, { headers: auth })
        .then(({ data }) => {
          delete data.endDate;
          delete data.isCurrent;
          delete data.customer;
          delete data.room;

          this.setState({ file: data.document });
          delete data.document;
          this.setState({ snapshot: data });
        })
        .catch((error) => {
          console.log(error);

          toast.error("Something went wrong");
        });
    }

    Services.get(Config.apiUrl + "/customers", { headers: auth })
      .then(({ data }) => {
        this.setState({ customers: data });
      })
      .catch((error) => {
        console.log(error);

        toast.error("Something went wrong");
      });

    Services.get(Config.apiUrl + "/rooms", { headers: auth })
      .then(({ data }) => {
        this.setState({ rooms: data });
      })
      .catch((error) => {
        console.log(error);

        toast.error("Something went wrong");
      });
  }
  handleStringToArray = (str = "") => {
    const array = str.split(",");
    const buffer = [];

    for (let index = 0; index < array.length; index++) {
      buffer[index] = array[index];
    }

    const data = new Uint8Array(buffer);

    return data;
  };
  handleSubmit = () => {
    const { snapshot, file } = this.state;
    const auth = { Authorization: `bearer ${localStorage["token"]}` };
    const data = { ...snapshot, document: file };

    this.setState({
      snapshot: {
        id: 0,
        customerId: "",
        roomId: "",
        amount: "",
        startDate: snapshot.startDate,
      },
      file: "",
    });

    if (snapshot.id === 0) {
      Services.post(Config.apiUrl + "/snapshots", data, { headers: auth })
        .then(() => {
          toast.success("Successful Registred.");
        })
        .catch((error) => {
          console.log(error);

          if (error.response && error.response.data) {
            toast.error(error.response.data);
          } else {
            toast.error("Something went wrong");
          }
        });
    } else {
      Services.put(Config.apiUrl + "/snapshots/" + data.id, data, {
        headers: auth,
      })
        .then(() => {
          toast.info("Successful Updates.");
        })
        .catch((error) => {
          console.log(error);

          if (error.response && error.response.data) {
            toast.error(error.response.data);
          } else {
            toast.error("Something went wrong");
          }
        });
    }
  };
}

export default Snapshot;
