import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { Card, Row, Col } from "react-bootstrap";
import {
    SelectField,
    SubmitBtn,
    TextField,
    DateField,
} from "../../components/Form";
import Config from "../../config/config.json";
import Services from "../../services/HttpServices";
import { toast } from "react-toastify";

class ChecklistForm extends Component {
    state = {
        chechlist: {
            id: 0,
            equibmentId: "",
            equibmenTypeId: "",
            shift: "",
        },
        equibments: [
            { value: 1, label: "FORKLIFT" },
            { value: 2, label: "ITV" },
            { value: 3, label: "MHC" },
            { value: 4, label: "MC" },
            { value: 5, label: "RTG" },
            { value: 6, label: "ECH" },
            { value: 7, label: "RS" }
        ],
        shifts: [
            { value: 1, label: "7:00 - 11:00 am" },
            { value: 2, label: "12:00 - 4:00 pm" },
            { value: 3, label: "5:00 - 7:00 pm" },
            { value: 4, label: "7:00 - 11:00 pm" },
            { value: 5, label: "12:00 - 4:00 am" },
            { value: 6, label: "5:00 - 7:00 am" }
        ],
        forklifts: [],
        ITVes: [],
        MHCes: [],
        MCes: [],
        RTGes: [],
        ECHes: [],
        RSes: [],
        equibmentTypes: [],
    };

    schema = Yup.object({
        id: Yup.number().label("Id"),
        customerId: Yup.number().required().label("Customer"),
        roomId: Yup.number().required().label("Room"),
        amount: Yup.number().required().min(0).max(10000).label("Amount"),
        startDate: Yup.date().required().label("Start Date"),
    });

    render() {
        const { chechlist, equibments, shifts, equibmentTypes } = this.state;


        return (
            <>
                <Card>
                    <Card.Header>
                        <Card.Title>
                            {chechlist.id === 0 ? "New checklist" : "Update checklist"}
                        </Card.Title>
                    </Card.Header>
                    <Formik
                        initialValues={chechlist}
                        enableReinitialize={true}
                        validationSchema={this.schema}
                        onSubmit={(chechlist, { resetForm }) => {
                            resetForm();
                            this.setState({ chechlist });
                            this.handleSubmit(chechlist);
                        }}
                    >
                        {({ values }) => (
                            <>
                                <Card.Body>
                                    <Row>
                                        <Col>
                                            <SelectField
                                                name="equibment"
                                                label="Equibment"
                                                options={equibments}
                                                required
                                            />
                                        </Col>
                                        <Col>
                                            <SelectField
                                                name="shfit"
                                                label="Shift"
                                                options={shifts}
                                                required
                                            />
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col>
                                            <SelectField
                                                name="equibmentType"
                                                label="Equibment Type"
                                                options={this.handleEquibmentType().map((fork) => ({ label: fork.name, value: fork.id }))}
                                                required
                                            />
                                        </Col>
                                        <Col>
                                            <DateField name="startDate" label="start Date" required />
                                        </Col>
                                    </Row>
                                </Card.Body>
                                <Card.Footer className="text-center">
                                    <SubmitBtn />
                                </Card.Footer>
                            </>
                        )}
                    </Formik>
                </Card>
            </>
        );
    }

    handleEquibmentType = (id) => {
        const { chechlist, equibments, shifts, forklifts, ITVes, MHCes, MCes, RTGes, ECHes, RSes } = this.state;


        if (id === 1) {
            this.setState({ equibmentTypes: forklifts })

            return forklifts;
        }
        else if (id === 2) {
            this.setState({ equibmentTypes: ITVes })

            return ITVes;
        }
        else if (id === 3) {
            this.setState({ equibmentTypes: MHCes })

            return MHCes;
        }
        else if (id === 4) {
            this.setState({ equibmentTypes: MCes })

            return MCes;
        }
        else if (id === 5) {
            this.setState({ equibmentTypes: RTGes })

            return RTGes;
        }
        else if (id === 6) {
            this.setState({ equibmentTypes: ECHes })

            return ECHes;
        }
        else if (id === 7) {
            this.setState({ equibmentTypes: RSes })

            return RSes;
        }
        else {
            return []
        }
    }

    componentDidMount() {
        const auth = { Authorization: `bearer ${localStorage["token"]}` };


        Services.get(Config.apiUrl + "/forklift", { headers: auth })
            .then(({ data }) => {
                this.setState({ forklifts: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/itv", { headers: auth })
            .then(({ data }) => {
                this.setState({ ITVes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/mhc", { headers: auth })
            .then(({ data }) => {
                this.setState({ MHCes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/mc", { headers: auth })
            .then(({ data }) => {
                this.setState({ MCes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/rtg", { headers: auth })
            .then(({ data }) => {
                this.setState({ RTGes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/ech", { headers: auth })
            .then(({ data }) => {
                this.setState({ ECHes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });

        Services.get(Config.apiUrl + "/rs", { headers: auth })
            .then(({ data }) => {
                this.setState({ RSes: data });
            })
            .catch((error) => {
                console.log(error);

                toast.error("Something went wrong");
            });
    }
    handleSubmit = () => {
        const { chechlist, file } = this.state;
        const auth = { Authorization: `bearer ${localStorage["token"]}` };
        const data = { ...chechlist, document: file };

        this.setState({
            chechlist: {
                id: 0,
                customerId: "",
                roomId: "",
                amount: "",
                startDate: chechlist.startDate,
            },
            file: "",
        });

        if (chechlist.id === 0) {
            Services.post(Config.apiUrl + "/rentals", data, { headers: auth })
                .then(() => {
                    toast.success("Successful Registred.");
                })
                .catch((error) => {
                    console.log(error);

                    if (error.response && error.response.data) {
                        toast.error(error.response.data);
                    } else {
                        toast.error("Something went wrong");
                    }
                });
        } else {
            Services.put(Config.apiUrl + "/rentals/" + data.id, data, {
                headers: auth,
            })
                .then(() => {
                    toast.info("Successful Updates.");
                })
                .catch((error) => {
                    console.log(error);

                    if (error.response && error.response.data) {
                        toast.error(error.response.data);
                    } else {
                        toast.error("Something went wrong");
                    }
                });
        }
    };
}

export default ChecklistForm;
